public static String prepareComment( String comment ) {
    String c = comment.trim();
    if( c.startsWith( "--") ) {
        return comment.replaceAll( "--", "" ) ;
    }
    else if( c.startsWith( "//") ) {
        return comment.replaceAll( "//", "" ) ;
    }
    else if( c.startsWith( "/*" ) ) {
        return comment.replaceAll( "/\\*", "" ).replaceAll( "\\*/", "" ) ;
    }
    return comment ;
}

