-- NOTE: This query takes a long time to run without the "TOP 1000".
SELECT TOP 1000 p.run, p.camCol, p.field, p.objID, p.rowC, p.colC, p.rowV, p.colV, p.rowVErr, p.colVErr, p.flags, p.psfMag_u, p.psfMag_g, p.psfMag_r, p.psfMag_i, p.psfMag_z, p.psfMagErr_u, p.psfMagErr_g, p.psfMagErr_r, p.psfMagErr_i, p.psfMagErr_z FROM PhotoPrimary as p WHERE -- where the velocities are reliable power(p.rowv, 2) / power(p.rowvErr, 2) + power(p.colv, 2) / power(p.colvErr, 2) > 4 ;
