SELECT gal.ObjID FROM Galaxy gal WHERE gal.r + gal.rho < 24 -- red surface brightness more than 24 mag/sq-arcsec and gal.isoA_r between 30 and 60 -- major axis between 30" and 60" and (power(gal.q_r,2) + power(gal.u_r,2)) > 0.25 -- square of ellipticity > 0.5 squared ;
